      <input type="text" [(ngModel)]="filtersBOX" placeholder="Enter the Author" (input)="autocompleteAuthor(filtersBOX)">
      <button class="button-add" (click)="completeSuggestion(filtersBOX)" type="button">Add</button>
      <div class="noResults" *ngIf="noResults">
        <p>Author not found. Please try another Author</p>
      </div>  

      <div class="noResults" *ngIf="repeated">
