        <button (click)="getAuthorsPublications()" [disabled]="authors.length === 0 ">Search Publications</button>
      </div>

      <div class="button-publications">
        <button (click)="getNetworksAuthor()" [disabled]="authors.length === 0 ">Create Graph</button>
      </div>
    </div>

</div>

