import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { HomeService } from './services/home.service';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  publications: any[] = [];
  title = 'Collaboration Networks';
  info = 'Web app for dblp stats';
  fechaHoraActual = "";

  constructor(
    private http: HttpClient,
    private homeService: HomeService
  ){
    this.obtenerFechaHoraActual();
  }

  activeLinkStatistics = true
  activeLinkNetwork = true



  ngOnInit() {
    this.homeService.activeLinkStatistics$.subscribe((activeLinkStatistics) => {
      this.activeLinkStatistics = activeLinkStatistics;
    });

    this.homeService.activeLinkNetwork$.subscribe((activeLinkNetwork) => {
      this.activeLinkNetwork = activeLinkNetwork;
    });
  }

  obtenerFechaHoraActual() {
    const fechaHora = new Date();
    this.fechaHoraActual = fechaHora.toLocaleString(); // Formatear la fecha y la hora
  }
}
<!DOCTYPE html>
<html>
  <body>

    <div class="nav">
      <nav>
        <a routerLink="/" routerLinkActive="active">Home</a>
        <a routerLink="/statistics" routerLinkActive="active" *ngIf="activeLinkStatistics" >Statistics</a>
        <a routerLink="/info" routerLinkActive="active">Info</a>
        <a routerLink="/authors" routerLinkActive="active">Authors</a>
        <a routerLink="/network" routerLinkActive="active"  *ngIf="activeLinkNetwork">Network</a>
      </nav>
    </div>

    <div class="container">

      <router-outlet></router-outlet>
    </div>

    <footer id="v4-app-footer-container" fxFlex="40px" fxFlex.lt-sm="24px" fxLayout="row" fxLayoutAlign="center stretch">
      <app-footer></app-footer>
    </footer>

  </body>
</html>
