import { Injectable } from "@angular/core";
import { Options, DataSet } from "vis";

@Injectable()
export class AppNetworkService {
  public getNetworkOptions(): Options {
    return {
      autoResize: true,
      height: "600px",
      width: "100%",
      physics: {
        enabled: true,
        barnesHut: {
          gravitationalConstant: -2000, // Ajusta la fuerza repulsiva entre nodos
          springConstant: 0.05, // Ajusta la fuerza del resorte entre nodos
          springLength: 100, // Ajusta la longitud del resorte entre nodos
        },
      },
      layout: {
        hierarchical: {
          enabled: false,
        },
      },
      nodes: {
        shape: "dot",
        scaling: {
          min: 20,
          max: 2000,
          label: {
            min: 20,
            max: 2000,
            enabled: false,
            drawThreshold: 10,
          },
        },
        font: {
          size: 20,
          color: "#ffffff",
        },
        color: {
          background: "rgb(0, 22, 68)",
          border: "#000000",
          highlight: {
            background: "#00ff00",
            border: "#000000",
          },
          hover: {
            background: "#0000ff",
            border: "#000000",
          },
        },
      },
    };
  }
}
