import {
  Component,
  ElementRef,
  OnInit,
  OnDestroy,
  ViewChild,
} from '@angular/core';
import { Network, DataSet, Data, Edge } from 'vis';
import { AppNetworkService } from '../services/network.service';
import { Subject } from 'rxjs';
import { AppNetworkInitService } from '../services/network.init.service';
import { Node } from '../models/network.model'

@Component({
  selector: 'app-networks',
  templateUrl: './networks.component.html',
  styleUrl: './networks.component.scss'
})
export class NetworksComponent implements OnInit, OnDestroy {

  @ViewChild('menuDiv', { static: true })
  menuDiv!: ElementRef;

  @ViewChild('treeContainer', { static: true })
  treeContainer!: ElementRef;
  nameAuthor: any;

  minCluster = 0;
  maxCluster = 0;
  menuStatus: boolean = true;
  selectNode: any;
  selectEdge: any;
  prevSelectNode: any;
  publications: any[] = [];
  publicationsNode: { [key: string]: string[] } = {};
  objectKeys = Object.keys;

  private data: any = {};
  private nodes: DataSet<Node> = new DataSet<Node>();
  private edges: DataSet<Edge> = new DataSet<Edge>();
  public selectedData: Subject<Data>;
  private network!: Network;

  constructor(
    private appNetworkService: AppNetworkService,
    public appNetworkInitService: AppNetworkInitService,
  ) {
    this.selectedData = new Subject<Data>();
  }

  public ngOnInit(): void {

    this.nameAuthor = this.appNetworkInitService.selectedAuthors;

    const networkOptions = this.appNetworkService.getNetworkOptions();
    networkOptions.height = '800px'; 

    this.nodes = this.appNetworkInitService.getNodes();
    this.edges = this.appNetworkInitService.getEdges();
    this.data = {
      nodes: this.nodes,
      edges: this.edges,
    };

    this.network = new Network(
      this.treeContainer.nativeElement,
      this.data,
      networkOptions
    );

    this.network.on('select', (params) => this.onSelect(params));
    this.network.on('click', (params) => this.onClick(params));
  }

  public ngOnDestroy(): void {
    if (this.network != null) this.network.destroy();
  }

  updateCluster(): void {

    const networkOptions = this.appNetworkService.getNetworkOptions();
    networkOptions.height = '800px'; 

    this.nodes = this.appNetworkInitService.getNodes();
    this.edges = this.appNetworkInitService.getEdges();
    this.data = {
      nodes: this.nodes,
      edges: this.edges,
    };

    this.network = new Network(
      this.treeContainer.nativeElement,
      this.data,
      networkOptions
    );


    this.network.on('select', (params) => this.onSelect(params));
    this.network.on('click', (params) => this.onClick(params));

  }

  // Function to differentiate the selection of edges and nodes
  private onClick(params: any): void {

    if (params.nodes.length < 1) {
      if (params.edges.length > 0) {
        this.onClickEdge(params);
      }
    }
  }

  // Function so that common publications appear when clicking on an edge
  private onClickEdge(params: any): void {

    const edgeId = params.edges[0];

    const edge = this.edges.get(edgeId);

    this.publications = [];

    this.selectEdge = {
      edge: edge,
      type: 'edge'
    };

    const researcherName = this.selectEdge.edge.to;

    this.appNetworkInitService.nameAuthors = this.appNetworkInitService.nameAuthors.map((author: any) => {
      if (author.researcher === researcherName) {
        this.publications = this.publications.concat(author.publications);
      }
      return author;
    });


    this.selectNode = null;

  }

  // Function so that common publications appear when clicking on an node
  private onSelect(params: any): void {

    if (params.nodes.length == 1) {
      const selectedNodeId = params.nodes[0];
      const connectedEdges = this.network.getConnectedEdges(selectedNodeId);
      const connectedNodes: any[] = [];
      this.publicationsNode = {};

      connectedEdges.forEach(edgeId => {
        const edge = this.edges.get(edgeId);
        if (edge) {
          if (edge.to == params.nodes) {
            connectedNodes.push(edge.from);
          } else {
            connectedNodes.push(edge.to);
          }

          this.appNetworkInitService.nameAuthors = this.appNetworkInitService.nameAuthors.map((author: any) => {
            var uniquePublicationsSet = new Set();

            if (author.researcher === edge.to) {
              var uniquePublications: any = []
              author.publications.forEach((publication: any) => {
                 uniquePublicationsSet.add(publication);
              });

              var uniquePublications: any = Array.from(uniquePublicationsSet);

              this.publicationsNode[author.researcher] = uniquePublications;
            }
            return author;

          });

        }

      });

      const result = {
        edges: connectedNodes,
        nodes: params.nodes,
        pointer: params.pointer,
      };

      if (this.selectNode) {
        this.prevSelectNode = this.selectNode;
      }
      this.selectNode = result;
      this.selectEdge = null;
    }
  }
}

<div class="container">

  <!-- Title -->
  <h1>{{nameAuthor}} Collaboration Network</h1>

  <hr>

  <!-- Container Network -->
  <div class="container-network">

      <!-- Cluster settings -->
      <form>
        <label for="minCluster">Minimum Cluster:</label>
        <input type="number" id="minCluster" name="minCluster" [(ngModel)]="appNetworkInitService.cluster.min">

        <label for="maxCluster">Maximum Cluster:</label>
        <input type="number" id="maxCluster" name="maxCluster" [(ngModel)]="appNetworkInitService.cluster.max">

        <button type="button" (click)="updateCluster()">Update Cluster</button>
      </form>

    <!-- Network -->
    <div class="network" #treeContainer></div>

    <div class="select" style="flex:1">
      <h1>Selections</h1> 
      <hr>
      <!-- Selected node -->
      <div *ngIf="selectNode">
        <h2>Selected node {{ selectNode.nodes }}</h2> 
        <div *ngFor="let edge of selectNode.edges">
          <div>Common publications of {{ selectNode.nodes }} and {{ edge }}:</div>
          <ul>
            <li *ngFor="let publication of publicationsNode[edge]">{{ publication }}</li>
          </ul>
          <ul>
            <li *ngFor="let publication of publicationsNode[selectNode.nodes]">{{ publication }}</li>
          </ul>
        </div>
      </div>

      <!-- Selected edge -->
      <div *ngIf="selectEdge && selectEdge.type === 'edge'">
        <h2> Selected edge {{ selectEdge.edge.from }} from {{ selectEdge.edge.to }}</h2> 
        <div> Common publications of  {{ selectEdge.edge.from }} and {{ selectEdge.edge.to }}:</div>
        <ul>
          <li *ngFor="let publication of publications">{{ publication }}</li>
        </ul>
      </div>

    </div>   
  </div>

</div>
