import { Injectable } from "@angular/core";
import { Options, DataSet } from "vis";

@Injectable()
export class AppNetworkService {
  public getNetworkOptions(): Options {
    return {
      autoResize: true,
      height: "500px",
      width: "100%",
      physics: {
        enabled: true,
        barnesHut: {
          gravitationalConstant: -2000,
          centralGravity: 0.3,
          springLength: 95, // Ajusta según sea necesario
          springConstant: 0.04 // Ajusta según sea necesario
        },
        minVelocity: 0.75, // Ajusta según sea necesario
        solver: "barnesHut"
      },
      layout: {
        randomSeed: undefined,
        improvedLayout: true,
        hierarchical: {
          enabled: true,
          levelSeparation: 170,
          direction: "UD", // UD, DU, LR, RL
          sortMethod: "directed", // hubsize, directed
          nodeSpacing: 100
        }
      },
      nodes: {
        scaling: {
          min: 150,
          max: 160,
          label: {
            enabled: false,
            min: 14,
            max: 30,
            maxVisible: 40,
            drawThreshold: 5
          },
          customScalingFunction: (
            min: number,
            max: number,
            total: number,
            value: number
          ) => {
            if (max === min) {
              return 0.5;
            } else {
              let scale = 1 / (max - min);
              return Math.max(0, (value - min) * scale);
            }
          }
        },
        size: 10,
        font: {
          size: 20,
          color: "#ffffff"
        }
      }
    };
  }
}
