import { Component, OnInit, ViewChild, ElementRef, AfterViewInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ApiService } from '../api.service';
import { SeleccionService } from '../seleccion.service';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-config',
  templateUrl: './config.component.html',
  styleUrls: ['./config.component.scss']
})
export class ConfigComponent implements OnInit{
  titulosSeleccionados: any[] = [];
  book: string = "";
  sortedProceedings: any[] = [];

  constructor(
    private route: ActivatedRoute,
    private apiService: ApiService,
    private seleccionService: SeleccionService,
    private http: HttpClient
  ) {}

  ngOnInit() {
    //LLamada a la funcion principal para la ejecucion de todo
    this.main();
  }

  obtenerBooks(){
    this.apiService.obtenerbooks(this.titulosSeleccionados, this.book ).subscribe({
      next: (response: any[]) => {
        this.sortedProceedings = response.sort((a, b) => {
          // Ordenar por el nombre del año de forma ascendente
          return a.yearName.localeCompare(b.yearName);
        });
      },
      error: (error: any) => {
        console.error('Error al obtener los resultados filtrados:', error);
      }
    });
  }

  async main(){
    try {
      this.titulosSeleccionados = this.seleccionService.obtenerTitulosSeleccionados();
      console.log("TItulosselecc"+this.titulosSeleccionados);
      this.book = this.seleccionService.obtenerBook();
      this.obtenerBooks();




  } catch (error) {
    console.error('Error al obtener los datos:', error);
  }
  }



}

