import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service';
import { Subscription } from 'rxjs';
import { Router } from '@angular/router';
import { SeleccionService } from '../seleccion.service';
import { FormsModule } from '@angular/forms';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit {
  // Propiedades
  info: string = 'Home class';
  filtros: string = '';
  publicaciones: string[] = [];
  resultadosFiltrados: string[] = [];
  titulosFiltrados: { title: string, pr_objeto: any, selected: boolean }[] = [];
  etiquetas: string[] = []; // Lista de etiquetas posibles para filtrar 
  titulosSeleccionados: any[] = []; // Lista de titulos seleccionados para generar estadisticas
  seleccionarTodos = false;
  selccionarDecadas = false;
  selccionarDecadas2 = false;
  selccionarDecadas3 = false;
  selccionarDecadas4 = false;
  conferenceOption: string = "main";
  count: number = 0;
  mostrarAnios: boolean = false;
  mostrarDecadas: boolean = false;
  selectedOption: string = '';
  noResultsFound: boolean = false;


  private nodosSubscription: Subscription | undefined;
  private informacionNodoSubscription: Subscription | undefined;

  constructor(
    private apiService: ApiService, 
    private router: Router,
    private seleccionService: SeleccionService
  ) { }

  ngOnInit() {
    //INICIAL
  }

  execFunctionsYear(){
    this.obtenerNodosFiltrados();
    this.toggleYears();
  }

  execFunctionsDecades(){
    this.obtenerNodosFiltrados();
    this.toggleDecades();
  }

  toggleYears() {
    this.mostrarAnios = !this.mostrarAnios;
      if(this.mostrarDecadas == true){
        this.mostrarDecadas = false;
      }
  }

  toggleDecades() {
    this.mostrarDecadas = !this.mostrarDecadas;
      if(this.mostrarAnios == true){
        this.mostrarAnios = false;
      }
  }

  handleSelection() {
    // Lógica para manejar la opción seleccionada
    if (this.conferenceOption === 'mainConference') {
      this.conferenceOption = "main";
    } else if (this.conferenceOption === 'mainAndWorkshops') {
      this.conferenceOption = "all";
    }
    this.seleccionService.marcarOpcionConferencia(this.conferenceOption);
  }

  obtenerNodosFiltrados() {
    this.apiService.obtenerNodosFiltrados(this.filtros).subscribe({
      next: (response: any[]) => {
        // this.resultadosFiltrados = response.map(item => JSON.stringify(item));
        this.resultadosFiltrados = response.map(item => item);

        this.titulosFiltrados = Object.values(response.reduce((obj, item) => {
          const yearNode = item.properties;
          obj[yearNode.name] = {
            title: yearNode.name,
            pr_objeto: item,
            selected: false
          };
          return obj;
        }, {}));
        if (this.resultadosFiltrados.length === 0) {
          this.noResultsFound = true;
        } else {
          this.noResultsFound = false;
        }
        console.log(this.noResultsFound)
      },
      error: (error: any) => {
        console.error('Error al obtener los resultados filtrados:', error);
      }
    });
  }

  hayTitulosSeleccionados(): boolean {
    return this.titulosFiltrados.some(titulo => titulo.selected);
  }

  seleccionarTitulo(titulo: string) {
    if (this.titulosSeleccionados.includes(titulo)) {
      // Eliminar el título de la lista de seleccionados
      this.titulosSeleccionados = this.titulosSeleccionados.filter(t => t !== titulo);
    } else {
      // Agregar el título a la lista de seleccionados
      this.titulosSeleccionados.push(titulo);
    }
  }

  seleccionarTodosChanged() {
    for (let titulo of this.titulosFiltrados) {
      titulo.selected = this.seleccionarTodos;
    }

  }

  seleccionarDecadas() {
    for (let titulo of this.titulosFiltrados) {
      if (titulo.title == "1989" || titulo.title == "1990" ||titulo.title == "1991" ||titulo.title == "1992" ||
      titulo.title == "1993" || titulo.title == "1994" ||titulo.title == "1995" ||titulo.title == "1996" ||
      titulo.title == "1997" || titulo.title == "1998" || titulo.title == "1999") {
        titulo.selected = this.selccionarDecadas;
      } 
    }
  }

  seleccionarDecadas2() {

    for (let titulo of this.titulosFiltrados) {
      if (titulo.title == "1999" || titulo.title == "2001" ||titulo.title == "2002" ||titulo.title == "2003" ||
      titulo.title == "2004" || titulo.title == "2005" ||titulo.title == "2006" ||titulo.title == "2007" ||
      titulo.title == "2008" || titulo.title == "2009" || titulo.title == "2000") {
        titulo.selected = this.selccionarDecadas2;
      } 
    }
  }

  seleccionarDecadas3() {

    for (let titulo of this.titulosFiltrados) {
      if (titulo.title == "2009" || titulo.title == "2010" ||titulo.title == "2011" ||titulo.title == "2012" ||
      titulo.title == "2013" || titulo.title == "2014" ||titulo.title == "2015" ||titulo.title == "2016" ||
      titulo.title == "2017" || titulo.title == "2018" || titulo.title == "2019") {
        titulo.selected = this.selccionarDecadas3;
      } 
    }
  }

  seleccionarDecadas4() {

    for (let titulo of this.titulosFiltrados) {
      if (titulo.title == "2019" || titulo.title == "2020" ||titulo.title == "2021" ||titulo.title == "2022" ||
      titulo.title == "2023" || titulo.title == "2024"){
        titulo.selected = this.selccionarDecadas4;
      } 
    }
  }

  tituloChanged() {
    let todosSeleccionados = true;
    for (let titulo of this.titulosFiltrados) {
      if (!titulo.selected) {
        todosSeleccionados = false;
        break;
      }
    }
    this.seleccionarTodos = todosSeleccionados;
  }

  generarEstadisticas() {
    // Filtra los títulos seleccionados
    const titulosSeleccionados = this.titulosFiltrados
    .filter(titulo => titulo.selected).map(titulo => titulo.pr_objeto);

    // Almacena los títulos seleccionados en el servicio de selección
    this.seleccionService.agregarTitulos(titulosSeleccionados);
    this.seleccionService.marcarNombreVenue(this.filtros);

    // Redirige a la página de estadísticas
    this.router.navigateByUrl('/estadisticas');
  }

  getRange(length: number, step: number): number[] {
    return Array.from({ length: Math.ceil(length / step) }, (_, i) => i * step);
  }


}

<body>
  <div class="container">
    <h1>Collaboration Networks</h1>

    <div class="box-conference">
      <input type="text" [(ngModel)]="filtros" placeholder="Enter the Conference" (keyup.enter)="execFunctionsYear()">
    </div>

    <div class="noResults" *ngIf="noResultsFound">
      <p>Conference not found. Please try another conference</p>
    </div>

    <div class="buttons">
      <div class="button-years">
        <button (click)="execFunctionsYear()" [disabled]="filtros.length === 0">Filter by years</button>
      </div>

      <div class="button-decade">
        <button (click)="execFunctionsDecades()" [disabled]="filtros.length === 0">Filter by decades</button>
      </div>
    </div>

    <div>
      <label>
        <div class="title-select">
          Select Option:
        </div>
        <select [(ngModel)]="conferenceOption" (change)="handleSelection()">
          <option value="mainConference" selected>Main Conference</option>
          <option value="mainAndWorkshops">Conference and Workshops</option>
        </select>
      </label>
    </div>

    <div class="button-stats">
      <button [disabled]="!hayTitulosSeleccionados()" (click)="generarEstadisticas()">Generate Stats</button>
    </div>

    <div class="decades" *ngIf="mostrarDecadas && !mostrarAnios">
      <div *ngIf="titulosFiltrados.length > 0">
        <label>
          <input type="checkbox" [(ngModel)]="selccionarDecadas" (change)="seleccionarDecadas()">
          <span>1989-1999</span>
        </label>
      </div>

      <div *ngIf="titulosFiltrados.length > 0">
        <label>
          <input type="checkbox" [(ngModel)]="selccionarDecadas2" (change)="seleccionarDecadas2()">
          <span>1999-2009</span>
        </label>
      </div>

      <div *ngIf="titulosFiltrados.length > 0">
        <label>
          <input type="checkbox" [(ngModel)]="selccionarDecadas3" (change)="seleccionarDecadas3()">
          <span>2009-2019</span>
        </label>
      </div>

      <div *ngIf="titulosFiltrados.length > 0">
        <label>
          <input type="checkbox" [(ngModel)]="selccionarDecadas4" (change)="seleccionarDecadas4()">
          <span>2019-2024</span>
        </label>
      </div>
    </div>

    <div *ngIf="titulosFiltrados.length > 0 && mostrarAnios">
      <label>
        <input type="checkbox" [(ngModel)]="seleccionarTodos" (change)="seleccionarTodosChanged()">
        <span>All</span>
      </label>
    </div>

    <div *ngIf="mostrarAnios && !mostrarDecadas">
      <div *ngFor="let i of getRange(titulosFiltrados.length, 10)">
        <div class="column">
          <div *ngFor="let titulo of titulosFiltrados.slice(i, i + 10)">
            <label>
              <input type="checkbox" [(ngModel)]="titulo.selected" (change)="tituloChanged()">
              <span [ngClass]="{ 'selected': titulo.selected }">{{ titulo.title }}</span>
            </label>
          </div>
        </div>
      </div>
    </div>



  </div>


</body>
