      <tr>
        <th>Procedimiento</th>
        <th>Año</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let proceeding of sortedProceedings">
        <td>{{ proceeding.proceeding }}</td>
        <td>{{ proceeding.yearName }}</td>
      </tr>
    </tbody>
  </table>
