import { Component, OnInit } from '@angular/core';
import { ApiService } from '../services/api.service';
import { Chart } from 'chart.js';
import { InfoService } from '../services/info.service';
import { SpinnerService } from '../services/spinner.service';
import { HomeService } from '../services/home.service';

@Component({
  selector: 'app-authors',
  templateUrl: './authors.component.html',
  styleUrl: './authors.component.scss'
})
export class AuthorsComponent implements OnInit{

  filtersBOX: string = '';
  completeConference: string[] = [];
  authors: string[] = [];

  constructor(
    private apiService: ApiService, 
    public homeService: HomeService
  ) { }


  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }

  autocompleteAuthor(term: string): void {
    this.apiService.autocompleteAuthors(term).subscribe({
      next: (response: string[]) => {
        this.completeConference = response;
      },
      error: (error: any) => {
        console.error('Error in autocompleteConference', error);
      }
    });
  }

  completeSuggestion(suggestion: string) {


    if (suggestion.trim() !== '') {
      this.authors.push(suggestion.trim());
    }

  }


  selectSuggestion(suggestion: string) {
    this.filtersBOX = suggestion;
  }

}
<div class="container">

    <!-- Title -->
    <h1>Search Authors</h1>

    <!-- Search Conference -->
    <div class="box-conference">
      <input type="text" [(ngModel)]="filtersBOX" placeholder="Enter the Conference or Journal" (input)="autocompleteAuthor(filtersBOX)">
      <button class="button-add" (click)="completeSuggestion(filtersBOX)" type="button">Add</button>

      <div>
        <ul>
          <li class="click-element " *ngFor="let suggestion of completeConference" (click)="selectSuggestion(suggestion)">
            {{ suggestion }}
          </li>
        </ul>
      </div>
    </div>
</div>


<div *ngIf="this.authors.length > 0" class="filters-list">

    <p>Authors</p>
    <hr>
    <div *ngFor="let author of this.authors; let last = last" class="filter-item">
      <input type="checkbox" [(ngModel)]="author.selected" />
      <span class="filter-name">{{ filter.name }}</span>
      <button class="button-delete" (click)="deleteCommunity(filter)">X</button>
      <ul>
        <li *ngFor="let filterItem of filter.filtersList">{{ filterItem }}</li>
      </ul>
      <hr class="slim" *ngIf="!last">
    </div>

</div>
