<div class="allNumbers">
    <ul>
        <li class="click-element" >Total of Publications: {{allPublications}}</li>
        <li class="click-element">Total of Authors: {{allAuthors}}</li>
        <li class="click-element">Total of Conferences: {{allConferences}}</li>
      </ul>
</div>

<div class="flex-container" >


  <div class="flex-item">
      <h3>Number of Conferences and Papers, Journal Articles and Thesis by Year</h3>

      <div class="canvas-container">
        <div class="overlay">
          <app-spinner></app-spinner>
        </div>
        <canvas id="tripleBarChart"></canvas>
      </div>
  </div>

</div>



<div class="flex-container" >

    <div class="flex-item">
        <h3>Number of Authors by Year</h3>

        <div class="canvas-container">
          <div class="overlay" *ngIf="loadingGraph2">
            <app-spinner></app-spinner>
          </div>
          <canvas id="barChart2"></canvas>
        </div>
    </div>

    <div class="flex-item">
      <h3>Number of Conferences by Year</h3>

      <div class="canvas-container">
        <div class="overlay" *ngIf="loadingGraph1">
          <app-spinner></app-spinner>
        </div>
        <canvas id="barChart3"></canvas>
      </div>
  </div>
</div>

<hr class="line3">

<div class="flex-container" >
  <div class="container-table2">
    <div class="table">
      <table id="tableInstitution">
        <thead>
          <tr>
            <th>Institution</th>
            <th>Number of Authors</th>
          </tr>
        </thead>
        <tbody></tbody>
      </table>
    </div>
  </div>
</div>
