import { Injectable } from "@angular/core";
import { Options, DataSet } from "vis";

@Injectable()
export class AppNetworkService {
  public getNetworkOptions(): Options {
    return {
      autoResize: true,
      height: "600px",
      width: "100%",
      physics: { enabled: true },
      layout: {
        randomSeed: undefined,
        improvedLayout: true,
        levelSeparation: 300, // Ajusta la separación entre niveles de jerarquía
        nodeSpacing: 200, // Ajusta la separación entre nodos
      },
      nodes: {
        scaling: {
          min: 10,
          max: 50,
        },
        font: {
          size: 20,
          color: "#ffffff"
        },
        color: {
          background: "rgb(0, 22, 68)",
          border: "#000000",
          highlight: {
            background: "#00ff00",
            border: "#000000"
          },
          hover: {
            background: "#0000ff",
            border: "#000000"
          }
        },
        shape: 'box', // Forma del nodo
        size: 50, // Ajusta el tamaño del nodo para agregar espacio alrededor del contenido
      }
    };
  }
}
