import { Injectable } from '@angular/core';
import { Subject } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class SpinnerService {
  isLoading = false;

  show(): void {
    this.isLoading = true
  }

  hide(): void {
    this.isLoading = false
  }


}
