nter;\n  vertical-align: center;\n  font-size: 20px;\n}\n\ndiv[_ngcontent-%COMP%] {\n  min-width: 40px;\n  min-height: 1px;\n  margin-right: 5px;\n}\n\n.special[_ngcontent-%COMP%] {\n  margin-bottom: 40px;\n}\n\n#tableInstitution[_ngcontent-%COMP%]   th[_ngcontent-%COMP%]:nth-child(3) {\n  padding-left: 120px; \n\n}\n\n#degree[_ngcontent-%COMP%]   th[_ngcontent-%COMP%]:nth-child(2), #degree[_ngcontent-%COMP%]   th[_ngcontent-%COMP%]:nth-child(3) {\n  padding-left: 60px;\n}\n\n#degree[_ngcontent-%COMP%]   th[_ngcontent-%COMP%]:nth-child(4) {\n  padding-left: 90px;\n}\n\n.men[_ngcontent-%COMP%] {\n  background-color: rgb(51, 153, 255);\n}\n\n.women[_ngcontent-%COMP%] {\n  background-color: rgb(255, 87, 51);\n  margin-left: 20PX;\n}\n\n@media screen and (max-width: 768px) {\n  td[_ngcontent-%COMP%], th[_ngcontent-%COMP%] {\n    padding: 10px;\n  }\n  .legend[_ngcontent-%COMP%] {\n    display: flex;\n    justify-content: center;\n    font-size: 10px;\n    margin-bottom: 20px;\n  }\n  .container-table[_ngcontent-%COMP%] {\n    flex-direction: column;\n  }\n  .container-table2[_ngcontent-%COMP%] {\n    flex-direction: column;\n  }\n  .table[_ngcontent-%COMP%] {\n    border-right: none;\n    padding-right: 0;\n    padding-bottom: 20px;\n  }\n  #tableInstitution[_ngcontent-%COMP%]   th[_ngcontent-%COMP%]:nth-child(3) {\n    padding-left: 0px;\n  }\n  #tableInstitution[_ngcontent-%COMP%]   td[_ngcontent-%COMP%]:nth-child(3) {\n    padding-left: 0px;\n  }\n}\n.conceptual[_ngcontent-%COMP%] {\n  display: flex;\n  justify-content: center;\n  align-items: center;\n  height: 200px;\n}\n\n.aparte[_ngcontent-%COMP%] {\n  margin-top: 500px;\n}\n/*# sourceMappingURL=data:application/json;charset=utf-8;base64, */"]
