import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service';
import { SelectionService } from '../selection.service';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);


@Component({
  selector: 'app-config',
  templateUrl: './config.component.html',
  styleUrls: ['./config.component.scss']
})
export class ConfigComponent implements OnInit{

  titulosSeleccionados: any[] = [];
  conferenceOption: string = "";
  venueName: any[] = [];
  publicationsByYear: { yearName: string, totalPublications: number }[] = [];
  allPublications = 0;
  barChart!: Chart;


  constructor(
    private apiService: ApiService,
    private seleccionService: SelectionService,
  ) {}



  ngOnInit() {
    this.main();
  }

  getPublicationsbyYear() {

    let totalPublications = 0;

    this.apiService.getPublicationsbyYear().subscribe({
      next: (response: any[]) => {
        this.publicationsByYear = response; 
        // Asigna la respuesta del servicio a la variable
        this.publicationsByYear.sort((a, b) => {
          return parseInt(a.yearName) - parseInt(b.yearName);
        });

        const years = this.publicationsByYear.map(item => item.yearName);
        const publications = this.publicationsByYear.map(item => item.totalPublications);


        totalPublications = this.publicationsByYear.reduce((total, year) => total + year.totalPublications, 0); // Suma el número total de publicaciones

        this.generateBarChart('barChartp', 'Publications by Year', years, publications);
        console.log('Publicaciones por año:', this.publicationsByYear);
        console.log('Total de publicaciones ', totalPublications);

      },
      error: (error: any) => {
        console.error('Error al obtener las publicaciones en getBooks:', error);
      }
    });
  }

  getPublications() {
    this.apiService.getPublications().subscribe({
      next: (response: any[]) => {
        console.log('Total de puffffffblicaciones:', response);

        if (response.length > 0) {
          // Accede a la propiedad correcta en la respuesta para obtener el número total de publicaciones
          this.allPublications = response[0].all_publications;
        } else {
          // Si la respuesta está vacía, asigna 0 a this.allPublications
          this.allPublications = 0;
        }
        console.log('Total de publicaciones:', this.allPublications);
      },
      error: (error: any) => {
        console.error('Error al obtener las publicaciones en getBooks:', error);
      }
    });
  }

  generateBarChart(idChart: string, label: string, labels: any[], data: any[]) {
    this.barChart = new Chart(idChart, {
      type: 'bar',
      data: {
        labels: labels,
        datasets: [
          {
            label: label,
            data: data,
            backgroundColor: 'rgb(0, 22, 68)',
            borderColor: 'rgb(0, 22, 68)',
            borderWidth: 1
          }
        ]
      },
      options: {
        plugins: {
          legend: {
            labels: {
              color: 'black',
              font: {
                size: 18,
                family: 'Roboto',
              }
            }
          }
        },
        scales: {
          y: {
            beginAtZero: true
          }
        }
      }
    });
  }



  async main(){
    try {
      this.titulosSeleccionados = this.seleccionService.getSelectedTitles();
      this.conferenceOption = this.seleccionService.getConferenceOption();
      this.venueName = this.seleccionService.getVenueName();

      this.getPublicationsbyYear();
      this.getPublications();


  } catch (error) {
    console.error('Error in getData:', error);
  }
  }

}

