import { Component, OnInit, TemplateRef } from '@angular/core';
import { ApiService } from '../api.service';
import { Observable, Subscription, map, startWith } from 'rxjs';
import { Router } from '@angular/router';
import { SelectionService } from '../selection.service';
import { FormControl } from '@angular/forms';
import { BsModalRef, BsModalService } from 'ngx-bootstrap/modal';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit {


  filtersString : string = '';
  filtersBOX: string = '';
  filtersList: string[] = [];
  communities: { name: string, filtersList: string[], selected: boolean  }[] = [];
  filterComunities: string[] = [];
  currentConferences: string[] = [];
  nameCommunity: string = '';
  completeConference: string[] = [];
  filteredResults: string[] = [];
  filteredTitles: { title: string, pr_objeto: any, selected: boolean }[] = [];
  selectAll = false;
  selectDecades = false;
  selectDecades2 = false;
  selectDecades3 = false;
  selectDecades4 = false;
  conferenceOption: string = "main";
  showYears: boolean = false;
  showDecades: boolean = false;
  repeated: boolean = false;
  noResultsFoundConference: boolean | undefined;
  noResultsFoundJournal: boolean | undefined;
  modalRef: BsModalRef | undefined;
  showButtons = false;
  filVenues: Observable<string[]> | undefined;
  control = new FormControl();


  constructor(
    private apiService: ApiService, 
    private router: Router,
    private selectionService: SelectionService,
    private modalService: BsModalService,
  ) { }

  ngOnInit() {
    this.filVenues = this.control.valueChanges
  }

  autocompleteConference(term: string): void {
    this.apiService.autocompleteConference(term).subscribe({
      next: (response: string[]) => {
        this.completeConference = response;
      },
      error: (error: any) => {
        console.error('Error in autocompleteConference', error);
      }
    });
  }

  isFilteringDisabled(): boolean {
    return this.filtersList.length === 0 && this.communities.every(community => !community.selected);
  }

  completeSuggestion(suggestion: string) {


    if (suggestion.trim() !== '') {
      const conference = suggestion.trim();

    if(this.filterComunities.includes(conference)){
      this.repeated = true;
    }else{
      this.repeated = false;
    }


      this.apiService.getFilteredNodesConference([conference]).subscribe({
        next: (response: any[]) => {

          if (response.length !== 0) {

            this.noResultsFoundConference = false;

            if (!this.filtersList.includes(conference)) {
              this.filtersList.push(conference);

              const newFilters = this.filterComunities.filter(item => !this.filtersList.includes(item));

              // Concatena los elementos filtrados a this.filtersList
              this.filtersList = this.filtersList.concat(newFilters);
              this.filtersString  = this.filtersList.join(','); 
            }
          } else {
            this.noResultsFoundConference = true;
          }
          for (const filter of this.filtersList) {

            // Verifica si el elemento no está presente en currentConferences
            if (!this.filterComunities.includes(filter)) {

                // Agrega el elemento a currentConferences
                this.currentConferences.push(filter);


            }
            // Utilizando la función filter junto con indexOf
            const filtersListSinDuplicados: string[] = this.currentConferences.filter((valor, indice, self) => {
              return self.indexOf(valor) === indice;
            });

            this.currentConferences = filtersListSinDuplicados

        }

        },
        error: (error: any) => {
          console.error('Error in getFilteredNodesConference:', error);
        },
        complete: () => {
          this.filtersBOX = "";
        }
      });
    }
  }

  selectSuggestion(suggestion: string) {
    this.filtersBOX = suggestion;
  }

  deleteFilter(filter: string) {
    const i = this.filtersList.indexOf(filter);
    if (i !== -1) {
      this.filtersList.splice(i, 1); 
      this.filtersString = this.filtersList.join(',');
    }

    const R = this.currentConferences.indexOf(filter);
    if (R !== -1) {
      this.currentConferences.splice(i, 1); 
    }
  }

  deleteCommunity(communityToDelete: { name: string, filtersList: string[], selected: boolean }) {

    this.communities = this.communities.filter(community => {

      return !(community.name === communityToDelete.name && community.filtersList === communityToDelete.filtersList && community.selected === communityToDelete.selected);
    });


    for (const filter of communityToDelete.filtersList) {

      const i = this.filtersList.indexOf(filter);
      if (i !== -1) {
        this.filtersList.splice(i, 1); 
        this.filtersString = this.filtersList.join(',');
      }


    }

  }



  createCommunity(filtersList: string[]){
    this.communities.push({ name: this.nameCommunity, filtersList: filtersList, selected: false });

    this.closeModal()
    this.nameCommunity = '';

    this.filtersList = [];
    this.currentConferences = [];

  }

  execFunctionsYear(){
    this.showButtons= true;
    this.clear()
    this.getFilteredNodesJournal();
    this.getFilteredNodesConference();
    this.toggleYears();
  }

  execFunctionsDecades(){
    this.showButtons= true;
    this.clear()
    this.getFilteredNodesJournal();
    this.getFilteredNodesConference();
    this.toggleDecades();
  }

  toggleYears() {
    this.showYears = !this.showYears;
      if(this.showDecades == true){
        this.showDecades = false;
      }
  }

  toggleDecades() {
    this.showDecades = !this.showDecades;
      if(this.showYears == true){
        this.showYears = false;
      }
  }

  clear(){
    this.filteredTitles = [];
  }

  clear2(){
    this.filteredTitles = [];
    this.showButtons= false;
  }

  getFilteredNodesConference() {

    console.log("filtersList0: " + this.filtersList)
    console.log("filterComunities: " + this.filterComunities)

    console.log("communities: " + JSON.stringify(this.communities))

    this.communities.forEach(community => {

      if(community.selected == true){
        console.log("ddd" + community.selected)
        this.filterComunities = this.filterComunities.concat(community.filtersList);
      }else{
        // Puedes utilizar filter para eliminar elementos de this.filterComunities que estén en community.filtersList
        this.filterComunities = this.filterComunities.filter(filterItem => !community.filtersList.includes(filterItem));
        this.filtersList = this.filtersList.filter(filterItem => !community.filtersList.includes(filterItem));
      }

    });


    console.log("filterComunities2: " + this.filterComunities)
    console.log("filtersList2: " + this.filtersList)

    this.filtersList = this.filtersList.concat(this.filterComunities);
    this.filtersString  = this.filtersList.join(','); 

    this.filtersList = this.filtersList.filter((value, index, self) => {
      return self.indexOf(value) === index;
    });

    this.filterComunities = this.filterComunities.filter((value, index, self) => {
      return self.indexOf(value) === index;
    });


    console.log("filterComunities3: " + this.filterComunities)

    console.log("filtersList3: " + this.filtersList)

    this.apiService.getFilteredNodesConference(this.filtersList).subscribe({
      next: (response: any[]) => {
        // this.resultadosFiltrados = response.map(item => JSON.stringify(item));
        this.filteredResults = response.map(item => item);
        if(this.filteredTitles.length < 1){
          this.filteredTitles = Object.values(response.reduce((obj, item) => {
            const yearNode = item.properties;
            obj[yearNode.name] = {
              title: yearNode.name,
              pr_objeto: item,
              selected: false
            };
            return obj;
          }, {}));
          if (this.filteredResults.length === 0) {
            this.noResultsFoundConference = true;
          } else {
            this.noResultsFoundConference = false;
          }
          console.log(this.noResultsFoundConference)

        }
      },
      error: (error: any) => {
        console.error('Error al obtener los resultados filtrados:', error);
      }

    });

  }

  getFilteredNodesJournal() {
    this.apiService.getFilteredNodesJournal(this.filtersString).subscribe({
      next: (response: any[]) => {
        // this.resultadosFiltrados = response.map(item => JSON.stringify(item));
        this.filteredResults = response.map(item => item);

        if(this.filteredTitles.length < 1){
          this.filteredTitles = Object.values(response.reduce((obj, item) => {
            const yearNode = item.properties;
            obj[yearNode.name] = {
              title: yearNode.name,
              pr_objeto: item,
              selected: false
            };
            return obj;
          }, {}));
          if (this.filteredResults.length === 0) {
            this.noResultsFoundJournal = true;
          } else {
            this.noResultsFoundJournal = false;
          }
          console.log(this.noResultsFoundJournal)
        }


      },
      error: (error: any) => {
        console.error('Error al obtener los resultados filtrados:', error);
      }
    });
  }

  existSelectTitle(): boolean {
    let select = false;
    if (this.filteredTitles.some(titulo => titulo.selected)){
      select = true;
    }
    return select;
  }

  selectAlls() {
    if (this.filteredTitles.length > 0){
      for (let title of this.filteredTitles) {
        title.selected = this.selectAll;
      }
    }
  }

  selectDecade() {
    for (let year of this.filteredTitles) {
      if (year.title == "1989" || year.title == "1990" ||year.title == "1991" || year.title == "1992" ||
      year.title == "1993" || year.title == "1994" ||year.title == "1995" || year.title == "1996" ||
      year.title == "1997" || year.title == "1998" || year.title == "1999") {
        year.selected = this.selectDecades;
      } 
    }
  }

  selectDecade2() {

    for (let year of this.filteredTitles) {
      if (year.title == "2000" || year.title == "2001" ||year.title == "2002" ||year.title == "2003" ||
      year.title == "2004" || year.title == "2005" ||year.title == "2006" ||year.title == "2007" ||
      year.title == "2008" || year.title == "2009") {
        year.selected = this.selectDecades2;
      } 
    }
  }

  selectDecade3() {

    for (let year of this.filteredTitles) {
      if (year.title == "2010" ||year.title == "2011" ||year.title == "2012" ||
      year.title == "2013" || year.title == "2014" ||year.title == "2015" || year.title == "2016" ||
      year.title == "2017" || year.title == "2018" || year.title == "2019") {
        year.selected = this.selectDecades3;
      } 
    }
  }

  selectDecade4() {

    for (let year of this.filteredTitles) {
      if (year.title == "2020" ||year.title == "2021" ||year.title == "2022" ||
      year.title == "2023" || year.title == "2024"){
        year.selected = this.selectDecades4;
      } 
    }
  }

  titleChanged() {
    let all = true;
    for (let titulo of this.filteredTitles) {
      if (!titulo.selected) {
        all = false;
        break;
      }
    }
    for (let titulo of this.filteredTitles) {
      if (!titulo.selected) {
        all = false;
        break;
      }
    }
    this.selectAll = all;
  }


  generateStatistics() {

    const titles = this.filteredTitles.
    filter(titulo => titulo.selected).map(titulo => titulo.pr_objeto);

    console.log("quepasa"+JSON.stringify(titles))


    this.selectionService.addTitles(titles);
    const splitFilters = this.filtersString.split(',').map(filtersString => filtersString.trim());
    this.selectionService.flagNameVenue(splitFilters);
    console.log("aaTT"+splitFilters)

    this.router.navigateByUrl('/statistics');
  }

  generateConf() {

    const titles = this.filteredTitles
    .filter(titulo => titulo.selected).map(titulo => titulo.pr_objeto);

    this.selectionService.addTitles(titles);
    const splitFilters = this.filtersString.split(',').map(filtersString => filtersString.trim());
    this.selectionService.flagNameVenue(splitFilters);

    this.router.navigateByUrl('/config');
  }

  openModal(template: TemplateRef<any>) {
    this.modalRef = this.modalService.show(template)
  }

  closeModal() {
    this.modalRef?.hide();
  }


}


<div class="container">

  <!-- Title -->
  <h1>Collaboration Networks</h1>

  <!-- Search Conference -->
  <div class="box-conference">
    <input type="text" [(ngModel)]="filtersBOX" placeholder="Enter the Conference or Journal" (keyup.enter)="completeSuggestion(filtersBOX)" (input)="autocompleteConference(filtersBOX)">
    <button class="button-add"  type="button" (click)="completeSuggestion(filtersBOX)">Add</button>
    <div *ngIf="autocompleteConference.length > 0">
      <ul>
        <li class="click-element " *ngFor="let suggestion of completeConference" (click)="selectSuggestion(suggestion)">
          {{ suggestion }}
        </li>
      </ul>
    </div>
  </div>

  <div class="noResults" *ngIf="noResultsFoundConference">
    <p>Conference or Journal not found. Please try another Conference or Journal</p>
  </div>

  <div class="noResults" *ngIf="repeated">
    <p>This conference is in one of the selected communities</p>
  </div>



  <div *ngIf="this.filtersList.length > 0 || this.communities.length >0 " class="filters-list">
    <div *ngIf="this.communities.length >0">
      <p>Communities</p>
        <li *ngFor="let filter of communities">
          <input type="checkbox" [(ngModel)]="filter.selected" />
          {{ filter.name }}
          <button class="button-delete" (click)="deleteCommunity(filter)">X</button>
          <ul>
            <li *ngFor="let filterItem of filter.filtersList">{{ filterItem }}</li>
          </ul>
        </li>
    </div>

    <div *ngIf="this.filtersList.length > 0 && this.communities.length >0">
      <hr>
    </div>

    <div *ngIf="this.filtersList.length > 0">
      <p>Current Conferences:</p>
      <ul>
        <li *ngFor="let filter of currentConferences">
          {{ filter }}
          <button class="button-delete" (click)="deleteFilter(filter)">X</button>
        </li>
      </ul>

      <div class="buttons">
        <div class="button-community">
          <button (click)="openModal(community)" [disabled]="filtersList.length === 0">Create Community</button>
        </div>
      </div>
    </div>

  </div>



  <ng-template #community>
    <div class="custom-modal">
      <div class="modal-container">
        <div class="modal-content">
          <div class="modal-header">
            <h4 class="modal-title pull-left">Create Community</h4>
            <button type="button" class="close-btn" aria-label="Close" (click)="closeModal()">
              <span class="close-icon" aria-hidden="true">&times;</span>
            </button>
          </div>
          <div class="modal-body">
            <input type="text" [(ngModel)]="nameCommunity" placeholder="Add name Community">
            <ul>
              <li *ngFor="let filter of currentConferences">
                {{ filter }}
                <button class="button-delete" (click)="deleteFilter(filter)">X</button>
              </li>
            </ul>
            <div class="button-community">
              <button (click)="createCommunity(currentConferences)">Create</button>
            </div>

          </div>
        </div>
      </div>
    </div>
  </ng-template>


  <!-- Buttons Filter -->
  <div class="buttons">
    <div class="button-years">
      <button (click)="execFunctionsYear()" [disabled]="filtersList.length === 0 && communities.length === 0">Filter by years</button>
    </div>
    <div class="button-decade">
      <button (click)="execFunctionsDecades()" [disabled]="filtersList.length  === 0 && communities.length === 0">Filter by decades</button>
    </div>
  </div>

  <!-- Filter by Decade -->
  <div class="decades" *ngIf="showDecades && !showYears">
    <div *ngIf="filteredTitles.length > 0 ">
      <label>
        <input type="checkbox" [(ngModel)]="selectDecades" (change)="selectDecade()">
        <span>1989-1999</span>
      </label>
    </div>

    <div *ngIf="filteredTitles.length > 0">
      <label>
        <input type="checkbox" [(ngModel)]="selectDecades2" (change)="selectDecade2()">
        <span>1999-2009</span>
      </label>
    </div>

    <div *ngIf="filteredTitles.length > 0">
      <label>
        <input type="checkbox" [(ngModel)]="selectDecades3" (change)="selectDecade3()">
        <span>2009-2019</span>
      </label>
    </div>

    <div *ngIf="filteredTitles.length > 0">
      <label>
        <input type="checkbox" [(ngModel)]="selectDecades4" (change)="selectDecade4()">
        <span>2019-2024</span>
      </label>
    </div>
  </div>

  <!-- Select All -->
  <div *ngIf="(filteredTitles.length > 0) && !showDecades">
    <label>
      <input type="checkbox" [(ngModel)]="selectAll" (change)="selectAlls()">
      <span>All</span>
    </label>
  </div>

  <!-- Filter by Year -->
  <div *ngIf="!showDecades">
    <div class="columns">
      <div class="column" *ngFor="let title of filteredTitles; let i = index">
        <label>
          <input type="checkbox" [(ngModel)]="title.selected" (change)="titleChanged()">
          <span [ngClass]="{ 'selected': title.selected }">{{ title.title }}</span>
        </label>
      </div>
    </div>
  </div>

   <!-- Generate Stats -->
   <div class="stats" >
    <div class="button-stats" *ngIf="showButtons">
      <button [hidden]="(this.noResultsFoundConference === true) ||
      (this.noResultsFoundConference === undefined)" [disabled]="!existSelectTitle()" (click)="generateStatistics()">Generate Stats</button>
    </div>
    <div class="button-stats" *ngIf="showButtons">
      <button [hidden]="(this.noResultsFoundConference === true) ||
      (this.noResultsFoundConference === undefined)" [disabled]="!existSelectTitle()" (click)="generateConf()">Info</button>
    </div>
    <div class="button-clear" *ngIf="filteredTitles.length > 0  ">
      <button (click)="clear2()">Cancel</button>
    </div>

  </div>
  <div class="modal-button">
    <button class="help" (click)="openModal(template)">?</button>
  </div>


</div>


<ng-template #template>
  <div class="custom-modal">
    <div class="modal-container">
      <div class="modal-content">
        <div class="modal-header">
          <h4 class="modal-title pull-left">Help</h4>
          <button type="button" class="close-btn" aria-label="Close" (click)="closeModal()">
            <span class="close-icon" aria-hidden="true">&times;</span>
          </button>
        </div>
        <div class="modal-body">
          <p>Buscar solo uno:</p>
          <ul>
            <li>ER</li>
            <li>World Wide Web</li>
            <!-- Agrega más elementos de la lista aquí -->
          </ul>
          <p>Buscar mas de uno (separar por comas):</p>
          <ul>
            <li>ER, 3DOR, BDM</li>
            <!-- Agrega más elementos de la lista aquí -->
          </ul>
        </div>
      </div>
    </div>
  </div>
</ng-template>
