import { Component, OnInit } from '@angular/core';
import { ApiService } from '../services/api.service';
import { Chart } from 'chart.js';
import { InfoService } from '../services/info.service';
import { SpinnerService } from '../services/spinner.service';
import { HomeService } from '../services/home.service';

@Component({
  selector: 'app-authors',
  templateUrl: './authors.component.html',
  styleUrl: './authors.component.scss'
})
export class AuthorsComponent{

  filtersBOX: string = '';
  completeConference: string[] = [];
  authors: string[] = [];
  publications: string[] = [];

  constructor(
    private apiService: ApiService, 
    public homeService: HomeService
  ) { }

  autocompleteAuthor(term: string): void {
    this.apiService.autocompleteAuthors(term).subscribe({
      next: (response: string[]) => {
        this.completeConference = response;
      },
      error: (error: any) => {
        console.error('Error in autocompleteConference', error);
      }
    });
  }

  getAuthorsPublications(term: string[]): void {
    this.apiService.getAuthorsPublications(this.authors).subscribe({
      next: (response: string[]) => {
        this.publications = response;
        this.generateTablePublications(this.publications);
      },
      error: (error: any) => {
        console.error('Error in getAuthorsPublications', error);
      }
    });
  }

  completeSuggestion(suggestion: string) {


    if (suggestion.trim() !== '') {
      this.authors.push(suggestion.trim());
    }

  }

  selectSuggestion(suggestion: string) {
    this.filtersBOX = suggestion;
  }


  deleteFilter(filter: string) {

    const i = this.authors.indexOf(filter);
    if (i !== -1) {
      this.authors.splice(i, 1); 
    }
  }


  generateTablePublications(publications: any[]) {
    const table = document.querySelector('#tablePublications tbody');

    if (table instanceof HTMLElement) {
      publications.forEach(({ title, DayOfPublication}) => {
        const row = document.createElement('tr');
        row.innerHTML = `<td>${title}</td><td>${DayOfPublication}</td>`;

        table.appendChild(row);
      });
    }
  }



}
<div class="container">

    <!-- Title -->
    <h1>Search Authors</h1>

    <!-- Search Conference -->
    <div class="box-conference">
      <input type="text" [(ngModel)]="filtersBOX" placeholder="Enter the Conference or Journal" (input)="autocompleteAuthor(filtersBOX)">
      <button class="button-add" (click)="completeSuggestion(filtersBOX)" type="button">Add</button>

      <div>
        <ul>
          <li class="click-element " *ngFor="let suggestion of completeConference" (click)="selectSuggestion(suggestion)">
            {{ suggestion }}
          </li>
        </ul>
      </div>
    </div>

    <div *ngIf="this.authors.length > 0" class="filters-list">
      <div>
        <p>Authors:</p>
        <hr>
        <ul>
          <li *ngFor="let filter of authors">
            {{ filter }}
            <button class="button-delete" (click)="deleteFilter(filter)">X</button>
          </li>
        </ul>
      </div>
    </div>  

    <!-- Buttons Filter -->
    <div class="buttons">
      <div class="button-publications">
        <button (click)="getAuthorsPublications(authors)" [disabled]="authors.length === 0 ">Filter by years</button>
      </div>
    </div>

</div>

<div class="flex-container" >
  <h3>TOP 20 Institutions with most Thesis</h3>
    <div class="container-table2">
      <div class="table">
        <table id="tablePublications">
          <thead>
            <tr>
              <th>Title</th>
              <th>DayOfPublication</th>
            </tr>
          </thead>
          <tbody></tbody>
        </table>
      </div>
    </div>
</div>
