import { Injectable } from "@angular/core";
import { Options, DataSet } from "vis";

@Injectable()
export class AppNetworkService {
  public getNetworkOptions(): Options {
    return {
      autoResize: true,
      height: "600px",
      width: "100%",
      physics: { enabled: true },
      layout: {
        randomSeed: undefined,
        improvedLayout: true,
        levelSeparation: 300, // Ajusta la separación entre niveles de jerarquía
        nodeSpacing: 200, // Ajusta la separación entre nodos
      },
      nodes: {
        scaling: {
          min: 150,
          max: 300,
          label: {
            enabled: false,
            min: 14,
            max: 30,
            maxVisible: 40,
            drawThreshold: 5
          },
          customScalingFunction: (
            min: number,
            max: number,
            total: number,
            value: number
          ) => {
            if (max === min) {
              return 0.5;
            } else {
              let scale = 1 / (max - min);
              return Math.max(0, (value - min) * scale);
            }
          }
        },
        size: 1,
        font: {
          size: 20,
          color: "#ffffff"
        },
        color: {
          background: "rgb(0, 22, 68)", // Cambia el color de fondo de los nodos
          border: "#000000", // Cambia el color del borde de los nodos
          highlight: {
            background: "#00ff00", // Cambia el color de fondo de los nodos cuando se resaltan
            border: "#000000" // Cambia el color del borde de los nodos cuando se resaltan
          },
          hover: {
            background: "#0000ff", // Cambia el color de fondo de los nodos cuando se pasa el cursor sobre ellos
            border: "#000000" // Cambia el color del borde de los nodos cuando se pasa el cursor sobre ellos
          }
        },
      }
    };
  }
}
