import { Component } from '@angular/core';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  publications: any[] = [];
  title = 'interfaz';
  info = 'Web app for dblp stats'

  constructor(private http: HttpClient) {}

  ngOnInit() {
    // this.http.get<any[]>('http://localhost:3000/api/publications')
    //   .subscribe((response) => {
    //     this.publications = response;
    //   });
  }
}
<!DOCTYPE html>
<html>
  <body>

    <div class="nav">
      <nav>
        <a routerLink="/" routerLinkActive="active">Home</a>
        <a routerLink="/statistics" routerLinkActive="active">Statistics</a>
        <a routerLink="/info" routerLinkActive="active">Info</a>
        <a routerLink="/authors" routerLinkActive="active">Authors</a>
      </nav>
    </div>


    <div class="container">

      <router-outlet></router-outlet>
    </div>
  </body>

</html>
