import { Component, OnInit, TemplateRef } from '@angular/core';
import { ApiService } from '../api.service';
import { Observable, Subscription, map, startWith } from 'rxjs';
import { Router } from '@angular/router';
import { SelectionService } from '../selection.service';
import { FormControl } from '@angular/forms';
import { BsModalRef, BsModalService } from 'ngx-bootstrap/modal';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit {


  filtersString : string = '';
  filtersBOX: string = '';
  filtersList: string[] = [];
  completeConference: string[] = [];
  filteredResults: string[] = [];
  filteredTitles: { title: string, pr_objeto: any, selected: boolean }[] = [];
  selectAll = false;
  selectDecades = false;
  selectDecades2 = false;
  selectDecades3 = false;
  selectDecades4 = false;
  conferenceOption: string = "main";
  showYears: boolean = false;
  showDecades: boolean = false;
  noResultsFoundConference: boolean | undefined;
  noResultsFoundJournal: boolean | undefined;
  modalRef: BsModalRef | undefined;
  showButtons = false;
  showAppend = false;
  filVenues: Observable<string[]> | undefined;
  control = new FormControl();


  constructor(
    private apiService: ApiService, 
    private router: Router,
    private seleccionService: SelectionService,
    private modalService: BsModalService,
  ) { }

  ngOnInit() {
    this.filVenues = this.control.valueChanges
  }

  autocompleteConference(term: string): void {
    this.apiService.autocompleteConference(term).subscribe({
      next: (response: string[]) => {
        this.completeConference = response;
      },
      error: (error: any) => {
        console.error('Error in autocompleteConference', error);
      }
    });
  }

  completeSuggestion(suggestion: string) {

    if (suggestion.trim() !== '') {
      const conference = suggestion.trim();

      this.apiService.getFilteredNodesConference([conference]).subscribe({
        next: (response: any[]) => {

          if (response.length !== 0) {

            this.noResultsFoundConference = false;

            if (!this.filtersList.includes(conference)) {
              this.filtersList.push(conference);
              this.filtersString  = this.filtersList.join(','); 
            }
          } else {
            this.noResultsFoundConference = true;
          }
        },
        error: (error: any) => {
          console.error('Error in getFilteredNodesConference:', error);
        },
        complete: () => {
          this.filtersBOX = "";
          this.showAppend = true;
        }
      });
    }
  }

  eliminarFiltro(filtro: string) {
    const indice = this.filtersList.indexOf(filtro);
    if (indice !== -1) {
      this.filtersList.splice(indice, 1); // Elimina el filtro de la lista
      const filtrosComoString = this.filtersList.join(',');
      this.filtersString = filtrosComoString; 
    }
  }


  execFunctionsYear(){
    this.showButtons= true;
    this.clear()
    this.obtenerNodosFiltradosJournal();
    this.obtenerNodosFiltradosConference();
    this.toggleYears();
  }

  execFunctionsDecades(){
    this.showButtons= true;
    this.clear()
    this.obtenerNodosFiltradosJournal();
    this.obtenerNodosFiltradosConference();
    this.toggleDecades();
  }

  toggleYears() {
    this.showYears = !this.showYears;
      if(this.showDecades == true){
        this.showDecades = false;
      }
  }

  toggleDecades() {
    this.showDecades = !this.showDecades;
      if(this.showYears == true){
        this.showYears = false;
      }
  }

  clear(){
    this.filteredTitles = [];
  }

  clear2(){
    this.filteredTitles = [];
    this.showButtons= false;
  }

  handleSelection() {
    // Lógica para manejar la opción seleccionada
    if (this.conferenceOption === 'mainConference') {
      this.conferenceOption = "main";
    } else if (this.conferenceOption === 'mainAndWorkshops') {
      this.conferenceOption = "all";
    }
    this.seleccionService.markOptionConference(this.conferenceOption);
  }

  obtenerNodosFiltradosConference() {
    this.apiService.getFilteredNodesConference(this.filtersList).subscribe({
      next: (response: any[]) => {
        // this.resultadosFiltrados = response.map(item => JSON.stringify(item));
        this.filteredResults = response.map(item => item);
        if(this.filteredTitles.length < 1){
          this.filteredTitles = Object.values(response.reduce((obj, item) => {
            const yearNode = item.properties;
            obj[yearNode.name] = {
              title: yearNode.name,
              pr_objeto: item,
              selected: false
            };
            return obj;
          }, {}));
          if (this.filteredResults.length === 0) {
            this.noResultsFoundConference = true;
          } else {
            this.noResultsFoundConference = false;
          }
          console.log(this.noResultsFoundConference)
        }
      },
      error: (error: any) => {
        console.error('Error al obtener los resultados filtrados:', error);
      }

    });
  }

  obtenerNodosFiltradosJournal() {
    this.apiService.getFilteredNodesJournal(this.filtersString).subscribe({
      next: (response: any[]) => {
        // this.resultadosFiltrados = response.map(item => JSON.stringify(item));
        this.filteredResults = response.map(item => item);

        if(this.filteredTitles.length < 1){
          this.filteredTitles = Object.values(response.reduce((obj, item) => {
            const yearNode = item.properties;
            obj[yearNode.name] = {
              title: yearNode.name,
              pr_objeto: item,
              selected: false
            };
            return obj;
          }, {}));
          if (this.filteredResults.length === 0) {
            this.noResultsFoundJournal = true;
          } else {
            this.noResultsFoundJournal = false;
          }
          console.log(this.noResultsFoundJournal)
        }


      },
      error: (error: any) => {
        console.error('Error al obtener los resultados filtrados:', error);
      }
    });
  }

  hayTitulosSeleccionados(): boolean {
    let seleccionados = false;
    if (this.filteredTitles.some(titulo => titulo.selected)){
      seleccionados = true;
    }
    return seleccionados;
  }

  seleccionarTodosChanged() {
    if (this.filteredTitles.length > 0){
      for (let titulo of this.filteredTitles) {
        titulo.selected = this.selectAll;
      }
    }

  }

  seleccionarDecadas() {
    for (let titulo of this.filteredTitles) {
      if (titulo.title == "1989" || titulo.title == "1990" ||titulo.title == "1991" ||titulo.title == "1992" ||
      titulo.title == "1993" || titulo.title == "1994" ||titulo.title == "1995" ||titulo.title == "1996" ||
      titulo.title == "1997" || titulo.title == "1998" || titulo.title == "1999") {
        titulo.selected = this.selectDecades;
      } 
    }
  }

  seleccionarDecadas2() {

    for (let titulo of this.filteredTitles) {
      if (titulo.title == "2000" || titulo.title == "2001" ||titulo.title == "2002" ||titulo.title == "2003" ||
      titulo.title == "2004" || titulo.title == "2005" ||titulo.title == "2006" ||titulo.title == "2007" ||
      titulo.title == "2008" || titulo.title == "2009") {
        titulo.selected = this.selectDecades2;
      } 
    }
  }

  seleccionarDecadas3() {

    for (let titulo of this.filteredTitles) {
      if (titulo.title == "2010" ||titulo.title == "2011" ||titulo.title == "2012" ||
      titulo.title == "2013" || titulo.title == "2014" ||titulo.title == "2015" ||titulo.title == "2016" ||
      titulo.title == "2017" || titulo.title == "2018" || titulo.title == "2019") {
        titulo.selected = this.selectDecades3;
      } 
    }
  }

  seleccionarDecadas4() {

    for (let titulo of this.filteredTitles) {
      if (titulo.title == "2020" ||titulo.title == "2021" ||titulo.title == "2022" ||
      titulo.title == "2023" || titulo.title == "2024"){
        titulo.selected = this.selectDecades4;
      } 
    }
  }

  tituloChanged() {
    let todosSeleccionados = true;
    for (let titulo of this.filteredTitles) {
      if (!titulo.selected) {
        todosSeleccionados = false;
        break;
      }
    }
    for (let titulo of this.filteredTitles) {
      if (!titulo.selected) {
        todosSeleccionados = false;
        break;
      }
    }

    this.selectAll = todosSeleccionados;
  }


  generarEstadisticas() {

    const titulosSeleccionados = this.filteredTitles
    .filter(titulo => titulo.selected).map(titulo => titulo.pr_objeto);

    this.seleccionService.addTitles(titulosSeleccionados);
    const filtrosSeparados = this.filtersString.split(',').map(filtersString => filtersString.trim());
    this.seleccionService.flagNameVenue(filtrosSeparados);
