<div class="container">

  <!-- Title -->
  <h1>Collaboration Networks</h1>

  <!-- Search Conference -->
  <div class="box-conference">
    <input type="text" [(ngModel)]="filtersBOX" placeholder="Enter the Conference or Journal" (keyup.enter)="completeSuggestion(filtersBOX)" (input)="autocompleteConference(filtersBOX)">
    <button class="button-add"  type="button" (click)="completeSuggestion(filtersBOX)">Add</button>
    <div *ngIf="autocompleteConference.length > 0">
      <ul>
        <li class="elemento-clicable" *ngFor="let sugerencia of completeConference" (click)="selectSuggestion(sugerencia)">
          {{ sugerencia }}
        </li>
      </ul>
    </div>
  </div>

  <div class="noResults" *ngIf="noResultsFoundConference">
    <p>Conference or Journal not found. Please try another Conference or Journal</p>
  </div>

  <div *ngIf="showAppend" class="filtros-list">
    <p>Current Conferences:</p>
    <ul>
      <li *ngFor="let filtro of filtersList">
        {{ filtro }}
        <button class="button-delete" (click)="eliminarFiltro(filtro)">X</button>
      </li>
    </ul>
  </div>



  <!-- Buttons Filter -->
  <div class="buttons">
    <div class="button-years">
      <button (click)="execFunctionsYear()" [disabled]="filtersList.length === 0">Filter by years</button>
    </div>
    <div class="button-decade">
      <button (click)="execFunctionsDecades()" [disabled]="filtersList.length === 0">Filter by decades</button>
    </div>
  </div>



  <!-- Filter by Decade -->
  <div class="decades" *ngIf="showDecades && !showYears">
    <div *ngIf="filteredTitles.length > 0 ">
      <label>
        <input type="checkbox" [(ngModel)]="selectDecades2" (change)="seleccionarDecadas()">
        <span>1989-1999</span>
      </label>
    </div>

    <div *ngIf="filteredTitles.length > 0">
      <label>
        <input type="checkbox" [(ngModel)]="selectDecades2" (change)="seleccionarDecadas2()">
        <span>1999-2009</span>
      </label>
    </div>

    <div *ngIf="filteredTitles.length > 0">
      <label>
        <input type="checkbox" [(ngModel)]="selectDecades3" (change)="seleccionarDecadas3()">
        <span>2009-2019</span>
      </label>
    </div>

    <div *ngIf="filteredTitles.length > 0">
      <label>
        <input type="checkbox" [(ngModel)]="selectDecades4" (change)="seleccionarDecadas4()">
        <span>2019-2024</span>
      </label>
    </div>
  </div>

  <!-- Select All -->
  <div *ngIf="(filteredTitles.length > 0) && !showDecades">
    <label>
      <input type="checkbox" [(ngModel)]="selectAll" (change)="seleccionarTodosChanged()">
      <span>All</span>
    </label>
  </div>

  <!-- Filter by Year -->
  <div *ngIf="!showDecades">
    <div class="columns">
      <div class="column" *ngFor="let titulo of filteredTitles; let i = index">
        <label>
          <input type="checkbox" [(ngModel)]="titulo.selected" (change)="tituloChanged()">
          <span [ngClass]="{ 'selected': titulo.selected }">{{ titulo.title }}</span>
        </label>
      </div>
    </div>
  </div>

   <!-- Generate Stats -->
   <div class="stats" >
    <div class="button-stats" *ngIf="showButtons">
      <button [hidden]="(this.noResultsFoundConference === true) ||
      (this.noResultsFoundConference === undefined)" [disabled]="!hayTitulosSeleccionados()" (click)="generarEstadisticas()">Generate Stats</button>
    </div>
    <div class="button-stats" *ngIf="showButtons">
      <button [hidden]="(this.noResultsFoundConference === true) ||
      (this.noResultsFoundConference === undefined)" [disabled]="!hayTitulosSeleccionados()" (click)="generarconf()">Info</button>
    </div>
    <div class="button-clear" *ngIf="filteredTitles.length > 0  ">
      <button (click)="clear2()">Cancel</button>
    </div>

  </div>
  <div class="modal-button">
    <button class="help" (click)="openModal(template)">?</button>
  </div>


</div>


