import {
  Component,
  ElementRef,
  OnInit,
  OnDestroy,
  ViewChild,
} from '@angular/core';
import { Network, DataSet, Data, Edge } from 'vis';
import { AppNetworkService } from '../services/network.service';
import { Subject } from 'rxjs';
import { AppNetworkInitService } from '../services/network.init.service';
import { Node } from '../models/network.model'
import { SelectedNodesDirective } from '../services/selected-nodes.directive';
import { ApiService } from '../services/api.service';
import { Chart } from 'chart.js';
import { InfoService } from '../services/info.service';
import { SpinnerService } from '../services/spinner.service';
import { HomeService } from '../services/home.service';
import { auth } from 'neo4j-driver';

@Component({
  selector: 'app-networks',
  templateUrl: './networks.component.html',
  styleUrl: './networks.component.scss'
})
export class NetworksComponent  implements OnInit, OnDestroy {

  @ViewChild('menuDiv', { static: true })
  menuDiv!: ElementRef;

  @ViewChild('treeContainer', { static: true })
  treeContainer!: ElementRef;

  menuStatus: boolean = true;

  selectNode: any;
  selectEdge: any;
  prevSelectNode: any;

  publications: any[] = [];

  private data: any = {};

  private nodes: DataSet<Node> = new DataSet<Node>();
  private edges: DataSet<Edge> = new DataSet<Edge>();

  public selectedData: Subject<Data>;

  private network!: Network;

  private nodeNo: number = 6;



  constructor(
    private appNetworkService: AppNetworkService,
    private appNetworkInitService: AppNetworkInitService,
    private apiService: ApiService,
  ) {
    this.selectedData = new Subject<Data>();
  }

  public ngOnInit(): void {

    this.nodes = this.appNetworkInitService.getNodes();
    this.edges = this.appNetworkInitService.getEdges();
    this.data = {
      nodes: this.nodes,
      edges: this.edges,
    };

    this.network = new Network(
      this.treeContainer.nativeElement,
      this.data,
      this.appNetworkService.getNetworkOptions()
    );

    this.network.on('select', (params) => this.onSelect(params));
    this.network.on('click', (params) => this.onClickEdge(params));
  }

  public ngOnDestroy(): void {
    if (this.network != null) this.network.destroy();
  }

  private onClickEdge(params: any): void {
    // Get the edge information
    const edgeId = params.edges[0];
    const edge = this.edges.get(edgeId);
    this.publications = [];



    // Update selectNode with edge information
    this.selectEdge = {
      edge: edge,
      type: 'edge'
    };

    const researcherName = this.selectEdge.edge.to; 

    this.appNetworkInitService.nameAuthors = this.appNetworkInitService.nameAuthors.map((author: any) => {
      if (author.researcher === researcherName) {
        // Concatenar las publicaciones del autor a this.publications
        this.publications = this.publications.concat(author.publications);
      }
      return author;
    });

    console.log(this.publications)
  }

  private onSelect(params: any): void {
    if (params.nodes.length == 1) {


      this.nodes.add({
        id: this.nodeNo,
        label: `Node ${this.nodeNo}`,
      });


      const nodeName = params.nodes[0].label; // Obtener el nombre del nodo

      this.edges.add({
        from: params.nodes[0].id, // Suponiendo que ya estás usando el ID del nodo como "from"
        to: nodeName, // Usar el nombre del nodo como destino
      });
      this.nodeNo++;
      const result = {
        edges: params.edges,
        nodes: params.nodes,
        pointer: params.pointer,
      };

      if (this.selectNode) {
        this.prevSelectNode = this.selectNode;
      }
      this.selectNode = result;

      const newEdges = this.edges
        .get()
        .filter((value) => {
          return this.network
            .getSelectedEdges()
            .some((val) => val == value['id']);
        })
        .map((value) => {
          return { to: value['to'], from: value['from'] };
        });

      const rootSelected: number = <number>this.network.getSelectedNodes()[0];

      let newNodes = this.nodes.get().filter((value) => {
        return newEdges.some((s) => s.to == value.id);
      });
      if (!newNodes.some((value) => value.id == rootSelected)) {
        const self = this.nodes.get().find((val) => val.id == rootSelected);
        if (self !== undefined) {
            newNodes.unshift(self);
        }
      } else {
        const root = this.nodes.get()[0];
        newNodes.unshift(root);
      }
      this.selectedData.next({ edges: newEdges, nodes: newNodes });
    }
  }
}

<div class="container">

  <div #treeContainer></div>

  <div #menuDiv style="flex:4" *ngIf="selectedData">
    <h1> Previous Selected Node</h1>
      <!--<div>{{prevSelectNode | json}}</div>-->
    <div selected-nodes [selected]="selectedData"> </div>
  </div>

  <div #treeContainer style="flex:4"></div>

  <div style="flex:1">
    <h1 *ngIf="selectNode"> Selected node</h1>
    <div>{{selectNode| json}} </div>
  </div>  

  <div #menuDiv style="flex:4" *ngIf="selectEdge && selectEdge.type === 'edge'">
    <div> Common publications of  {{ selectEdge.edge.from }} and {{ selectEdge.edge.to }}:</div>
    <ul>
      <li *ngFor="let publication of publications">{{ publication }}</li>
    </ul>
  </div>

</div>
